import styled from "styled-components";

export const Input = styled.input`
  width: 100%;
  padding: 1rem;
  border: none;
  border-radius: 10px;
  background: #e9eff6;
  font-size: 1.1rem;
  outline: none;
  margin-bottom: 1.2rem;

  @media (max-width: 600px) {
    padding: 0.8rem;
    font-size: 1rem;
  }
`;

export const Button = styled.button`
  width: 100%;
  padding: 0.9rem;
  background: #1084f6;
  color: #fff;
  border: none;
  border-radius: 10px;
  font-size: 1.1rem;
  font-weight: 600;
  cursor: pointer;
  margin-bottom: 1.2rem;

  @media (max-width: 600px) {
    padding: 0.7rem;
    font-size: 1rem;
  }
`;
