import styled from "styled-components";

export const MainContainer = styled.main`
  max-width: 1000px;
  margin: 3rem auto;
  padding: 0 1rem;

  @media (max-width: 900px) {
    max-width: 98vw;
    margin: 1.5rem auto;
    padding: 0 0.5rem;
  }
`;

export const ResponsiveFlex = styled.div`
  display: flex;
  gap: 2.5rem;

  @media (max-width: 700px) {
    flex-direction: column;
    gap: 1.2rem;
  }
`;
